  function zhm_get_title($text, $extra_classes = '') {
    ob_start(); ?>

    <h2 class="primary-font text-primary bold mt-3 mt-md-5 mb-0 <?php echo $extra_classes; ?>">
      <?php echo $text; ?>
    </h2>

  <?php
    return ob_get_clean();
  }

  function zhm_get_doc_types() {
    return array(
      array(
          'id' => 'city_council',
          'label' => 'Рішення міської ради',
      ),
      array(
          'id' => 'exec_committee',
          'label' => 'Рішення виконавчого комітету',
      ),
      array(
          'id' => 'mayor',
          'label' => 'Розпорядження міського голови',
      ),
      array(
          'id' => 'project',
          'label' => 'Проекти рішень',
      ),
      array(
          'id' => 'other',
          'label' => 'Інші документи',
      ),
      array(
          'id' => 'acts',
          'label' => 'Діючі регуляторні акти',
      ),
    );
  }

  function zhm_get_doc_type_label($doc_type) {
    $doc_ids = array_column(zhm_get_doc_types(), 'id');
    $doc_types = array_column(zhm_get_doc_types(), 'label');
    $doc_type_index = array_search($doc_type, $doc_ids);
    $doc_type_label = ($doc_type_index !== false ? $doc_types[$doc_type_index] : null);

    return $doc_type_label;
  }

  function zhm_get_button($text, $link, $extra_classes = '') {
    $arrow = zhm_get_arrow();

    ob_start(); ?>

    <a
      href="<?php echo $link; ?>"
      class="link-button primary-font bold <?php echo $extra_classes; ?>"
    >
      <span><?php echo $text; ?></span>
      <span class="ms-3"><?php echo $arrow; ?></span>
    </a>

    <?php

    return ob_get_clean();
  }

  function zhm_nice_number($n) {
    $n = (0 + str_replace(",","",$n));
   
    if(!is_numeric($n)) return 0;
   
    if($n > 1000000) return round(($n / 1000000), 1).'M';
    
    if($n > 1000) return round(($n / 1000), 1).'K';
   
    return number_format($n);
}